mapper.select:
SELECT * FROM ( SELECT ( SELECT device_addrese FROM ptp_device WHERE edit_device_id = r.get_device_id ) AS get_device_addrese, ( SELECT device_addrese FROM ptp_device WHERE edit_device_id = r.out_device_id ) AS out_device_addrese, ( SELECT device_addrese FROM ptp_device WHERE edit_device_id = ir.device_id ) AS in_device_addrese, ( SELECT create_time FROM ptp_device WHERE edit_device_id = ir.device_id ) AS in_report_time, r.repair_get, r.report_type, r.repair_line, r.repair_out, r.get_report_time, r.out_report_time, r.user_id, r.user_phone, r.state, r.trip_code FROM ptp_report AS r, ptp_in_report AS ir WHERE r.trip_code = ir.trip_code ) AS t WHERE 1=1
AND t.user_phone = #{userPhone}
AND t.user_id = #{userId}
AND t.get_report_time >= date_format( #{startTime}, '%Y-%m-%d %H:%i:%s' )
AND t.get_report_time <= date_format( #{endTime}, '%Y-%m-%d %H:%i:%s' )
AND (t.get_device_addrese LIKE concat('%',#{condition},'%') OR t.user_phone LIKE concat('%',#{condition},'%'));
ORDER BY t.get_report_time DESC

SELECT ( SELECT device_addrese FROM ptp_device WHERE edit_device_id = r.get_device_id ) AS get_device_addrese, ( SELECT device_addrese FROM ptp_device WHERE edit_device_id = r.out_device_id ) AS out_device_addrese, r.repair_get, r.report_type, r.repair_line, r.repair_out, r.get_report_time, r.out_report_time, r.user_id, r.user_phone, r.state, r.trip_code FROM ptp_report AS r WHERE 1=1 AND r.user_id=#{userId}
AND r.trip_code=#{tripCode}
ORDER BY r.get_report_time DESC
